// Essa parte controla os dialogos da loja

var cara_vendedor = {"timePerChar":0.02,"linesPerBlock":4,"justification":0,"stayOpenTime":0.0,"showArrow":false,"leftMargin":0.0,"wrapText":true,"useCustomFace":false,"faceset":"","faceX":0,"faceY":0,"useCharacterFace":true,"characterId":2};
var cara_jogador = {"timePerChar":0.02,"linesPerBlock":4,"justification":0,"stayOpenTime":0.0,"showArrow":false,"leftMargin":0.0,"wrapText":true,"useCustomFace":false,"faceset":"","faceX":0,"faceY":0,"useCharacterFace":true,"characterId":0}

function dialogo_feno(){
	var numero_mascara = 37;
	var numero_mascara_bonita = 38;
	var numero_feno = 34;

	game.equipItem(0,3,-1);
	if(game.countItems(numero_mascara) >= 1 || game.countItems(numero_mascara_bonita) >= 1){
		if(game.countItems(numero_mascara) >= 1 ){
			game.equipItem(0,3,numero_mascara);	
		} else {
			game.equipItem(0,3,numero_mascara_bonita);
		}

		game.showText(["Então querido, eu me recuso fazer alguma coisa de feno além da mascara."], cara_vendedor);
		return;
	}

	game.showText(["Hmm... Talvez eu consiga fazer uma mascara com isso."], cara_vendedor);
	game.showText(["....?"], cara_jogador);
	game.showText(["Que foi? Acabou o estoque de mascara, acha que eu tô sem por quê?"], cara_vendedor);
	game.showText(["Ai... Tá bom então, só faz o trem."], cara_jogador);
	game.showText(["Iiihh, grosso."], cara_vendedor);
	game.addRemoveItem(numero_feno, 1 * -1);
	game.addRemoveItem(numero_mascara, 1);
	game.sleep(1);
	game.equipItem(0,3,numero_mascara);
	game.showText(["Pronto, aqui está!"], cara_vendedor);
	game.showText(["Você recebeu uma Mascara de Feno!"]);
}

function dialogo_osso(){
	game.showText(["Hmm... Agradeço o desafio, mas eu nunca vou fazer uma roupa emo gótica das trevas."], cara_vendedor);
	game.showText(["Mas não precisa ser gótica."], cara_jogador);
	game.showText(["...?"], cara_vendedor);
	game.showText(["O que mais você espera que eu faça com um osso?"], cara_vendedor);
	game.showText(["Ah, eu sei lá."], cara_jogador);
}

function dialogo_vinho(){
	game.showText(["Ei! Eu não bebo em serviço!"], cara_vendedor);
	game.showText(["Tem ninguém aqui fora além de mim."], cara_jogador);
	game.showText(["Tem a Jeniffer."], cara_vendedor);
	game.showText(["Jeniffer? Que Jeniffer?"], cara_jogador);
	game.showText(["A vaca."], cara_vendedor);
	game.showText(["E ela é cliente?"], cara_jogador);
	game.showText(["Sim, e uma das melhores! *-*"], cara_vendedor);
	game.showText(["Tá..."], cara_jogador);
}

function dialogo_pena(){
	var numero_mascara = 37;
	var numero_mascara_bonita = 38;
	var numero_pena = 33;

	if(game.getInt("primeiro_dialogo_pena") != 1 ){
		game.shakeScreen(20.0, 4.0, 4.0, 0.5);
		game.showText(["MEU."],cara_vendedor);
		game.showText(["DEUS."],cara_vendedor);
		game.showText(["DO."],cara_vendedor);
		game.showText(["CÉU!"],cara_vendedor);
		game.showText(["Onde que você arranjou essa beldade?"],cara_vendedor);
		game.showText(["Ah, um pato me deu?"],cara_jogador);
		game.showText(["Abençoado seja o pato!"],cara_vendedor);
		game.showText(["Você tem mais de onde essa veio?"],cara_vendedor);
		game.showText(["Tenho quase certeza que o pato só deixa eu levar uma por vez."],cara_jogador);
		game.showText(["Ah, que pena. (ba dum tss)"],cara_vendedor);
		game.setInt("primeiro_dialogo_pena",1);
	}
	game.showText(["Bem... Eu acho que eu posso fazer um enfeite com essa pena..."],cara_vendedor);

	game.equipItem(0,3,-1);
	if(game.countItems(numero_mascara) >= 1){
		game.showText(["Não vai ser a melhor coisa mas acho que eu posso fazer um enfeite na sua mascara."], cara_vendedor);


		game.addRemoveItem(numero_mascara, 1 * -1);
		game.addRemoveItem(numero_pena, 1 * -1);
		game.addRemoveItem(numero_mascara_bonita, 1);
		game.equipItem(0,3,numero_mascara_bonita);
		game.showText(["Aqui está. A mascara de feno mais bonita da vila."], cara_vendedor);
		game.showText(["Você recebeu uma Mascara de Feno muito bonita!"]);
	} else{
		if(game.countItems(numero_mascara_bonita) >= 1){
			game.equipItem(0,3,numero_mascara_bonita);
		}
		game.showText(["Volta aqui quando você conseguir alguma roupa descente para eu enfeitar."], cara_vendedor);
	}

}

function dialogo_sair(){
	game.showText(["Opa, quero mais nada não, valeu ae."], cara_jogador);
	game.showText(["Volte sempre!"], cara_vendedor);
}

// Essa parte controla as opções que irão aparecer ao usuario

var constantes = [
	["Mostrar osso", dialogo_osso, 32],
	["Mostrar feno", dialogo_feno, 34],
	["Mostrar vinho", dialogo_vinho, 36],
	["Mostrar pena", dialogo_pena, 33],
	["Sair", dialogo_sair]
];

var opcoes = [];
var opcoes_ref = [];

for(var i=0; i< constantes.length; i++){
	var const_atual = constantes[i]
	if(const_atual[2]){
		if(game.countItems(const_atual[2]) >= 1){
			opcoes.push(const_atual[0]);
			opcoes_ref.push(const_atual[1]);	
		}
	} else {
		opcoes.push(const_atual[0]);
		opcoes_ref.push(const_atual[1]);
	}
}


var res = game.getChoice([""], opcoes, false, {});
opcoes_ref[res]();

